INSERT INTO sales_pipeline (customer_id, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, sales_route, comparison_services, initial_channel, id) VALUES (E'2a42041c-5b9f-4e2e-b344-35fa382d0754', E'追加指導', 0, E'サービス説明ページを読んで', E'実施', E'厳しそうだったが、望みはあるという感じ。その場で決めきれなかったのがとても悔しい。\n\nお金がなくて親御さんと交渉後の追加指導だった。最初は親御さんと交渉してもダメでしたと言われるだけで望みも薄かったが、親御さんが「就活に過度にお金を払うのは嫌」というタイプだったので、今自分で使えるお金を就活に使い、プライベートの予定に対するお金を親御さんに出してもらうのはどうかと提案したところ、それなら可能性が全然あるとのこと。\n\n個人的にかなりグリップをできている感触があり、（当然断るのだが、）個人メンターとかでもダメですかとか言われるくらいなので、来月頭（3月のバイトのシフトが出て、お金のめどが立ちそうなタイミング）にもう一度話すことにした。\n\n親御さんに依存せずに自分でお金を出す、そのために3月のバイトを頑張るということで、30万も全然払えるなら払いたいという感じなので、最後まで追い切る。', E'2026-02-07', 0.6, E'2026-03-13', E'岡本', E'3/13にクイックに話を聞く', E'X', E'比較なし', E'Google検索', E'f6d2da8a-81b9-46f8-a6fe-49cbf1423fd7');
INSERT INTO sales_pipeline (customer_id, stage, projected_amount, decision_factor, deal_status, initial_channel, id) VALUES (E'f16c3a61-ad1b-4505-bf62-d4a9728df572', E'実施不可', 0, E'不明', E'未実施', E'不明', E'720b09d0-107b-4f63-bc9e-ca206a0822ab');
INSERT INTO sales_pipeline (customer_id, agent_interest_at_application, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, comparison_services, initial_channel, id) VALUES (E'dbd7912c-c164-4a54-906e-2ed212d8cc16', E'コンコード', E'成約', 396000, E'X', E'実施', E'・通常プランで成約したが、クラウドサインのシステムメンテナンスのせいで契約書と決済は未済。リスクは僅少だが。しっかり追いかける\n・東工大→都市計画系のブティックコンサル。めちゃくちゃ優秀。理路整然としていて素敵な方\n・高校時代から国際協力に関心あり、大学では土木系専攻。自分は都市や交通×国際で活躍したいと、日本工営という現職のみ就活で受けて内定\n・その後、自動運転のPOCなどの経験を積む中で、POCから実装に至らないところに課題を感じて、戦略コンサルを受けたいと思うにいたった。戦略７社＋IGPI（現職で子会社と働いて印象がよく、戦略コンサルを志望する理由にもなった）\n・コンコードと書類を仕上げており、練習企業を２月から、第一志望群（戦略７社）を４－５月に面接するスケジュールで進めたいとのこと。\n・本当はまだ書類出していなかったのでコンコードからはがしたかったが、かなり信頼関係がかたく失注に向かうリスクに鑑みて、提案にとどめた。', E'2026-01-18', 1.0, E'2026-01-19', E'田中', E'最後成約したのに、クラウドサインがシステムメンテナンス中で契約書ドラフトまで行ったが送付できず。日本の明朝、すぐに送付し、入金まで追いかける', E'比較なし', E'X', E'85f5c235-3e64-43b5-8d8e-3674b823040c');
