INSERT INTO sales_pipeline (customer_id, agent_interest_at_application, meeting_scheduled_date, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, agent_confirmation, comparison_services, initial_channel, alternative_application, id) VALUES (E'a64550b5-ee20-4eb1-b315-9575049d39f7', E'MyVision', E'・1/24にベインの1day選考会（書類は通過し、Webテスト受験前）\n・その他ドリームインキュベータ1/16、FIELD MANAGEMENT STRATEGY1/14、SHAPE Partners1/16に一次面接予定\n・pwc、KPMG FASは書類通過し日程調整中', E'成約', 368900, E'Youtube(ケース面接対策TV)を見て', E'実施', E'・慶應→福岡が地元でUターン就職で福岡放送。そこからM&A仲介へ転職。コミッション比率が高くベース給与が低いため、転職を考え始めた。営業も好きだが考えることが好きで、コンサルに興味\n・12月からMVと転職活動を開始。１がつのベインの選考会に参加予定（書類通っている）。一方で、MVのケース対策が微妙だと思っており、ベイン以外の戦略７社へのアプライは止めている状況。一方で、Pｗｃ、ＫＰＭＧ　ＦＡＳ，ＤＩ，フィールドマネジメント、Shapeパートナーズには書類出して通っており、EYは書類出したが待ち\n・転職検討ラインは戦略７社。その下ならちょっとまた考えようかなって感じ→なのでエージェント割はオファーせず\n・英語に不安あるのでマッキンゼーは受けない\n・ケースすごくできる\n・現職のコミッションが入るタイミングの関係で、７月１日より前の入社は難しい。\n\n最後、成約の段階で、アプライタイミングの戦略について質問を受けた。そこで、ＢＣＧだけはベインと迷うので同じタイミングで出す、それ以外はそもそも７月以降の入社ならもう少し待ってもいいかも、今のエージェントが早めに出させようとしてきたらうちからまた遅いタイミング出すこともできるのでその時はまたお声がけください、みたいなことを言った\n\nそしたら、BCGとそれ以外の戦略４社もうちでお願いできないかと言われたので、エージェントもうちになった（→本人から、ベインの選考会が終わった段階でMVに転職辞めるっていうとの申し出があった）', E'2026-01-12', 1.0, E'2026-01-12', E'田中', E'メンターは優秀な人を当ててください。ここ最近ではかなり優秀な方です', E'検討が難しいが、念の為話を聞きたい', E'比較なし', E'Youtube', E'【書類通過】Bain＆Company、KPMG FAS、PwCコンサル（Strategy＆）、ドリームインキュベータ、FIELD MANAGEMENT STRATEGY、SHAPE Partners【書類見送り】ピー・アンド・イー・ディレクションズ、フォーティエンスコンサルティング、経営共創基盤', E'3ad01f6e-f30b-4494-ba87-b514950135d1');
INSERT INTO sales_pipeline (customer_id, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, sales_route, comparison_services, initial_channel, id) VALUES (E'd63fb51b-1ac7-4b59-9578-536c29888b8b', E'失注見込', 0, E'Youtube', E'実施', E'あまり加入する意思がなさそうだった。\nS&の最終は落ちてしまったようで、今はMcKを受けているらしく、特化プランを提案した。\n懸念としては、次の2次面接に落ちてしまうと、10万円が無駄になってしまうこと、のようで、2次が通ったらインターン/最終に向けて、我々と一緒にやりましょうと提案した。', E'2026-01-10', 0.2, E'2026-01-31', E'岡本', E'2次の結果が来る1月末くらいをめどに連絡', E'X', E'比較なし', E'Google検索', E'37d4ae34-a994-4be8-b113-7e8257d0051b');
INSERT INTO sales_pipeline (customer_id, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, sales_route, comparison_services, initial_channel, id) VALUES (E'456bba8a-0773-4f58-a5f3-639574ceeb1a', E'失注見込(自動)', 0, E'X', E'実施', E'手ごたえは良かったし、かなりプランの説明や就活における課題への解決策の提示が刺さっていたような気がする。\nそれだけに親と話してからといわれて切られてしまったのが残念。', E'2026-01-10', 0.8, E'2026-01-17', E'岡本', E'絶対取る、1週間で返事しますといわれたので、1週間後に何もなければ電話で聞いてみます。', E'X', E'比較なし', E'X', E'e355cd79-ed25-4b8a-b840-b4a29a4af77c');
INSERT INTO sales_pipeline (customer_id, agent_interest_at_application, meeting_scheduled_date, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, agent_confirmation, sales_route, comparison_services, initial_channel, id) VALUES (E'5115a396-7d1d-43e1-80e8-3447fa5e3e7d', E'MyVision', E'未定', E'失注見込(自動)', 0, E'サービス説明ページを読んで', E'実施', E'中国ご出身。支払い能力は高いが、そもそもどこまで日本で頑張りたいかから本人のスタンスがはっきりしない（外国人としての大変さと日本語能力の問題、実家が太くて働かなくてても中国ではよい生活ができる）・日本語力が足りずケースの議論で刺しに行けないことなどがハードル。とりあえず今はいろんな説明聞いていて～との発言もあり、無料体験目的っぽい感じもありそう\n\n詳しい経歴（口頭で聞いた）\n・２０１７－１９中国の大学で物理学学士\n・そのあと、２０２０年から京都大学社会学研究科\n・２０２１年関西大学大学院\n・その後中退して北陸先端科学大学院\n・２０２２年－２４年修士→博士（退学）\n・２０２４年祖父の体調悪化で博士中退して中国戻る、\n・２０２４年、祖父が亡くなり再び日本へ。とりあえず日本にいたいからビザ取らなければならなくて就職活動を頑張り、２０２５年４月からビックカメラ\n\n転職活動状況\n・船井総研、カーニー、ベインは自己応募で面接まで進み、１次面接で敗退\n・それ以外はエージェント３－４社と他のコンサルにもいろいろ出している状況\n\nなぜ日本にいらしたのかの雑談の中で、車が好き、という話が出た。\n車を２台持っている（ポルシェ・BMW）とのことで、初手で受けた印象より支払い能力がある可能性がある（ご実家が太い可能性）→ケースのあと、「実家が事業をやっており、中国に帰ったら遊んで暮らせるくらいの状況ではある。しかし、将来起業したいのでコンサルで頑張りたい」との発言があり、伏線回収された\nどのプランをイメージしているか、で通常プランとおっしゃっていた\n\nネックは、本人がそもそもどこまで日本で頑張りたいか、言語的なバリアも含めて考え中で、お金というよりはどこまでケース対策にコミットするか決め切れていない状況。\n日本語力は日常会話はOKだが、ケースの議論には耐えられないくらいのレベル', E'2026-01-16', 0.2, E'2026-01-16', E'田中', E'どこまで転職活動に真剣なのかわからない・無料体験目的に近いテンションではあるが、支払い能力ありまくるのでわんちゃんを狙って追加指導を獲った', E'興味があり、話を聞きたい', E'Google検索、ケース面接対策', E'比較なし', E'FB広告', E'e1563d20-867f-4cdd-b54b-b495c7bed489');
INSERT INTO sales_pipeline (customer_id, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, sales_route, comparison_services, initial_channel, id) VALUES (E'1d8ed24f-cbe4-4a24-be7f-6533ca385409', E'検討中', 0, E'X', E'実施', E'この時期に27卒が戦略目指せるのか？？\n春選考受けるみたいなので、その対策を万全にしましょうと進めたが、、\n手応えはそこまでない', E'2026-01-08', 0.4, E'2026-01-08', E'東山', E'リマインド', E'X', E'比較なし', E'X', E'5d738a14-1106-408f-bc89-3246d8034ed2');
