INSERT INTO sales_pipeline (customer_id, agent_interest_at_application, meeting_scheduled_date, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, agent_confirmation, comparison_services, initial_channel, id) VALUES (E'c94af4e9-307c-499c-8edc-0c3f7d2f48b9', E'MyVision', E'未定', E'成約', 936000, E'サービス説明ページを読んで', E'実施', E'2027年4月入社をめどに転職活動していて情報収集っぽい感じだったので、今すぐ始めましょうという方向にもっていった。そうですねとなって12月中にいったんの結論を出す方向性で追加指導（詳細は下記）\n\nーー\n・新卒ベイカレント５年目。PMO案件中心で戦略やりたくなった\n・今年の11月からマイビジョンと転職活動を開始したが、家庭の事情で本命企業を受けるタイミングを遅らせ、2027年4月入社を目指してMBBや他の戦コンを受けたいとのこと（→なので受講は2026年の夏ごろからですかね、といわれた）\n・一方でマイビジョンとはブティック系のコンサルを受け始めており、1月には面接が入っている。面接が入っているのはプラグマテック？、SHAPE Partners、アクセンチュア戦略とのこと。ただしこれらの企業の面接のために弊社サービスを利用するつもりはなく、来年のMBB選考に向けてだとはっきりおっしゃっている。練習・気に入った企業があれば考えたいため受けているとのこと\n・12月にお子さんが生まれて、1月まで育休中\n↓\n上記をうけて、\n・あくまで本人の忙しさ・2027年入社の可能性は尊重。そのうえで、\n①年齢は若ければ若い方が有利\n②１月に受けているアクセンチュア戦略から内定が出たらどうする？結構迷わないか？\n③1月まで育休でお時間あるとおっしゃっていたので、今がインプットするには良い機会では？\n④受講後も追加指導は購入できるので、直前期の対策も対応できる\nと訴求\n\n結果的に「確かに今始めた方がいいかも」となり、12月中に結論を出しましょうということで30日に追加指導に持ち込んだ。もし今すぐ契約とならずとも、結構ファンにはなっていただいているのでどこかでは契約獲れるだろう。', E'2025-12-30', 0.6, E'2025-12-30', E'田中', E'年内の成約を目指して追加指導。万が一ここで成約しなくても、良い印象持ってくださっているので追いかけ続ける', E'興味があり、話を聞きたい', E'比較なし', E'Facebook/インスタグラム', E'e0763660-a65b-46d6-806c-b7f0753d5b34');
INSERT INTO sales_pipeline (customer_id, agent_interest_at_application, meeting_scheduled_date, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, agent_confirmation, comparison_services, initial_channel, id) VALUES (E'ac4c4cf7-9e5b-4bb0-8f96-e62bc2b2103c', E'MyVision', E'未定', E'成約', 1396800, E'サービス説明ページを読んで', E'実施', E'・通常プラン・フルエージェントで成約（大阪大学→住友商事でMVにも戦略出せること確認済み・現在日本在住である旨も確認済み）\n・お手元に携帯やカードがないので未入金だが、リスク僅少。契約書・入金リンク・入塾フォームはZoomでは送付済み\n・LINEでスタンプ送っていただくよう連絡済みだが、いったん入塾関連の情報と今日の資料はメールアドレス宛にも送るようにする予定\n\n・MBBかTier２戦略に行きたい。それ以外は転職しないかなって感じなのでどこかに受かっていただくよう全力尽くす\n・マイビジョン（とは明示的にあえて聞かなかったが）から、ロゴス、ぷらぐまてっく？、フィールドマネジメント、SHAPEを出して面接が決まっている。P&Eは書類落ち？この辺のご本人の記憶やや曖昧なので契約書にかいてもらうよう伝えてある\n・マイビジョンのやりとりや実力に不安があり、はがすまでもなくぜひエージェントも、という感じだった\n・４月入社目指しつつこだわりはない。２０２６年度中にはって感じ。', E'2026-01-06', 1.0, E'2026-01-06', E'田中', E'追加指導なしで成約。入金までちゃんと追いかける。\n経験上、一発で選んでくれる人は優秀（意思決定が合理的・よいフィードバックの価値をきちんと評価できるレベルであるため）なので、ケースはまだまだ全然ダメだが頑張ってご支援しましょう。メンターもスターをアサインしてください', E'利用したい', E'比較なし', E'Google検索', E'0fd0a51a-cc59-46e2-b7ab-ca40e6edc652');
