INSERT INTO sales_pipeline (customer_id, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, sales_route, initial_channel, id) VALUES (E'18680e3f-ba58-48fd-be49-023d421e25da', E'失注見込(自動)', 0, E'無料noteを読んで', E'実施', E'今日マッキンゼーの2次。無料目的感もありつつ、特急とmck特化訴求。', E'2025-10-16', 0.4, E'2025-10-30', E'喜山', E'3日後リマインド。', E'S&の記事(空飛ぶクルマ)を読んで好印象だった。', E'Google検索', E'd7ab6f4e-c747-4ca3-89df-77a8a74a8e42');
INSERT INTO sales_pipeline (customer_id, agent_interest_at_application, meeting_scheduled_date, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, agent_confirmation, comparison_services, initial_channel, id) VALUES (E'6120001f-a4aa-4ac3-ad18-b385e25a1c53', E'ムービン', E'10月10日以降、ブティックファームの面接が順次入っています（外資戦略や総合系戦略は書類選考結果待ち）', E'成約', 198000, E'無料noteを読んで', E'実施', E'・戦略、総合ともにほぼ出し切っているためスクール料金のみの案内\n・年齢が３４？３５歳と高く、戦略はすべて書類落ち済み、総合はEYとKPMGはおちていて、デロイトとアクセンチュア、PWCが残っている。他にはマッキンゼーデジタルに自己応募で面接が来ているのと、フロンティアマネジメント、ベイカレント、UFＪコンサルその他たくさんのブティックファームを受けている\n・現職がグーグルで社格が高いため、フロンティアマネジメントとベイカレントでも転職を迷うかもというライン。フロンティアマネジメントとベイカレントのことは褒めておいた\n・競合サービスも見た上で弊社が一番クオリティ高いのでやるとしたら弊社とおっしゃっているが、書類通って面接が決まってるのがマッキンゼーのデジタル部門（SOLVE？）しかなく、投資するか迷われている。マッキンゼーＳＯＬＶＥの面接が来週木曜日にある\n・また、「田中さんに入っていただけるんですか？」的な質問が何回かあった。内定者メンターのクオリティの高さは強調したので、喜山さんの実力で押し切りたい', E'2025-10-10', 0.8, E'2025-10-11', E'田中', E'・サービスには満足いただいているが、マッキンゼーＳＯＬＶＥのためだけに投資するか迷われている。なので、特急プランにして来週までに仕上げ切って確実にマッキンゼー取りに行きましょう、という方針で営業した。また、追加指導も追加料金でできるので他の総合コンサルに行きたくなったらその時にまた追加するのもいいですよ、みたいなのは結構ささっていた\n・喜山さんには、内定者メンターのクオリティを見せつけるということ（実際にアサインされるメンターも優秀であることを推してほしい）、マッキンゼーに向けて確実に準備していきましょう、ということで契約につなげてほしい。マッキンゼーＳＯＬＶＥ？への知見があればそれをぜひ示したい。確度は高いので確実にクロージングしたい（ワンチャン短期もあるしご本人は短期で最初検討されていたが、書類通っていないからそもそもやめようかなという雰囲気になったので特急で押し切る方針にした）\n・ご本人はケースはまだまだですが、コミュニケーションはマチュアでＥＱ面は優秀な方です', E'検討が難しいが、念の為話を聞きたい', E'比較なし', E'Google検索', E'5c4ebdee-bc01-4964-b7dc-bbd577bd2d22');
INSERT INTO sales_pipeline (customer_id, agent_interest_at_application, meeting_scheduled_date, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, agent_confirmation, initial_channel, id) VALUES (E'5bbd2b84-8ebd-471c-885f-956f4e37843c', E'ムービン', E'10月末', E'成約', 299000, E'Youtube(ケース面接対策TV)を見て', E'実施', E'・戦略、総合すでにムービンから応募済みでスクールのみしか案内できない\n・１０・１８のベインの週末選考会に呼ばれており、そこに行きたい、Bain志望度高い\n・１２月までに内定を得て次年度から転職したく、急いでいる\n・サービスについての具体的な質問も多く、意欲高い\n・MBA検討しているくらいなので、キャリアへの投資意欲高い', E'2025-10-11', 0.8, E'2025-10-11', E'田中', E'・月曜日の他社との面談はせず、週末中に意思決定すると言っていただいた。ほぼ即決が決まりそうだったが他社の資料だけ確認してすぐ連絡するとのこと', E'検討が難しいが、念の為話を聞きたい', E'Facebook/インスタグラム', E'25fc5de0-a657-4e9b-af62-f7a3b73e7d7e');
INSERT INTO sales_pipeline (customer_id, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, initial_channel, id) VALUES (E'1c66e5e4-5fd2-458d-970e-14f6bb1971af', E'成約', 99600, E'Youtube(ケース面接対策TV)を見て', E'実施', E'解答資料のレベルが高いがゆえに、逆に今からでも間に合うかを気にしていた。秋冬での内定が増えていることも伝えてモチベ上げ。', E'2025-10-19', 0.6, E'2025-10-21', E'喜山', E'2日程度で連絡', E'Youtube', E'a4a0f248-f4bb-4e74-99d0-f25635ab1a54');
