INSERT INTO sales_pipeline (customer_id, agent_interest_at_application, meeting_scheduled_date, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, agent_confirmation, initial_channel, id) VALUES (E'cdcb2601-ff6c-4d55-8445-e65fd69595bc', E'Remedy', E'25/10', E'失注', 0, E'Instgram広告', E'実施', E'22年新卒PWC、戦略コンサルはすべて書類出していてエージェント併用が完全に無理。\nベインが第一志望、ベインの面接は10・１８の週末選考会にて。\nKPMGのFASとパルテノンをセーフティとして先に受け始めており、面接が控えている\nエージェントは1か月半前から、Remedyと契約\n\nご自身の課題意識としては、ふわっとしたお題はむしろ得意だがかっちりした売上向上系やフェルミに不安があるとのこと。ベインはこの辺がむしろ重視されることをお伝えした\n\n値段が高いとのことで、ケース面接だけの部分購入（教科書などなしで）ができないか、逆に教科書・動画のみの購入はできないか、といろいろ聞かれた。自主学習プランの提案でダウンセルを試みたが、それでも高いと思われている。たぶん10万円以上は出さなそう。\nケースメンタリングのクオリティには納得いただいた。', E'2025-10-03', 0.05, E'2025-10-04', E'田中', E'エージェント併用が全くできない・予算も厳しめに見ている方だが、ケースメンタリングのクオリティには納得いただいた。もう一度喜山さんでケースを見ていただきたい。\n特急もたぶん厳しいので自主学習プランでも成約できればという感じ', E'検討できない', E'Youtube', E'1642a48f-3948-4d47-9362-4c5dbc13a4b0');
