INSERT INTO sales_pipeline (customer_id, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, comparison_services, initial_channel, id) VALUES (E'f42ae38b-c132-4546-876d-d44d6b06bf04', E'日程未確', 0, E'不明', E'未実施', E'leapとprismと検討中。MBB面接官監修、27のメンター優秀確約が刺さった。やるならstrategistsと言ってくれた。', E'2025-12-02', 0.6, E'2025-10-31', E'東山', E'今月中に決めるとのこと。', E'LEAP, Prism', E'不可', E'6699d4fd-87f5-4242-b841-1c10cf215c06');
INSERT INTO sales_pipeline (customer_id, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, comparison_services, initial_channel, id) VALUES (E'92e846cd-56ac-4712-827d-350d702acd33', E'失注', 0, E'Xでの発信を読んで', E'実施', E'Prismとの比較で、費用を重視して選びたいとのこと。弊社特急プランのコスパに魅力を感じていただいている。\nケースメンタリングの刺さりもよかった（褒め８割＋お、って思ってもらえる指摘２割）\n一応今日は深夜なのでもう少し考えたいが、少なくとも今週中に返答する旨、おそらく数日内に返答する旨をお伝えいただいた。確度は高いとみてよいだろう', E'2025-09-29', 0.8, E'2025-10-03', E'田中', E'今週中にフォローアップ', E'Prism', E'X', E'23b2707f-8e02-4a8a-80fa-35f264dc3c06');
