INSERT INTO sales_pipeline (customer_id, meeting_scheduled_date, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, agent_confirmation, comparison_services, initial_channel, id) VALUES (E'e8ca8fe4-0622-42b1-b700-4cc47ef78d3c', E'10月初旬、下旬', E'失注見込', 0, E'無料noteを読んで', E'実施', E'・Bainはこの間、１次で落ちてしまった（！）振り返りをしながら要因を考えた（１つ選んで話した施策が大外ししていたように見える）\n・直近はS&が欧州にくるので面接を受ける予定\n・マッキンゼーSOLVEを受けた。\n・欧州で事業会社に転職するか、日本に戻るならコンサル。日本に戻るとコンサル以外だと学費払えないのがネックだと思っているので欧州Stayもあるかなと\n・くるチャンスは確実にものにしたいので、コンサル対策自体もやめるわけではない\n・キャリア迷子っぽかったので20分ほどキャリア相談に乗った。ビヘイビア対策の時間にキャリアアドバイスしてもいいですよと言ってみた', E'2025-10-11', 0.05, E'2025-11-29', E'田中', E'Bainにすでに1次で落ちてしまっている。ご本人が欧州就活に注力したい意向が現時点で強く、値段というよりはリソース的にケース対策に時間をさけなそうという雰囲気。ベインだと狙いたくなるが次のS&だとなかなかテンションを上げにくい。\nとりあえず海外就職のアドバイスなどをたくさんして、Giveだけして終えることにした。\nいつか帰ってきてくれたらいいなと思うものの見込み低い。', E'検討が難しいが、念の為話を聞きたい', E'比較なし', E'note検索・noteでおすすめされた', E'e67acf54-e948-4962-843e-d20620ff1e3b');
INSERT INTO sales_pipeline (customer_id, agent_interest_at_application, meeting_scheduled_date, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, agent_confirmation, initial_channel, id) VALUES (E'129a6348-0a06-40de-9c07-d1b4d5f986ee', E'ムービン', E'来週', E'失注', 0, E'無料noteを読んで', E'実施', E'ムービンから30社中小ファームだしており、3社面接予定（いずれも中小で名前よくわからず）\n総合系やベイカレントは出していなかったので、それを全部うちで出していただけるなら一部エージェント割でとご案内\nかなり穏やかな性格なので雑談多め・ゆるめの雰囲気にした\nメンタリングは気に入っていただけたが、おそらく値段がネック。割引を受けたいがムービンに恩義を感じていて（気まずくて）ためらっている', E'2025-09-27', 0.6, E'2025-10-10', E'田中', E'10/10頃にLINEでご状況お伺いする', E'興味があり、話を聞きたい', E'Google検索', E'370fc85d-d1c4-4cb0-a967-8e64fdd42cd8');
INSERT INTO sales_pipeline (customer_id, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, comparison_services, initial_channel, id) VALUES (E'999b83bb-1f23-40e6-b860-ceab3da2cef3', E'失注', 0, E'サービス説明ページを読んで', E'実施', E'あまり手応えはない。一応３日後再度やり取りをする予定だが、総コン志望のためあまりサービスに興味を示していなかったように感じる。', E'2025-09-27', 0.05, E'2025-09-30', E'河合', E'３日後にリマインド', E'比較なし', E'Google検索', E'a48c13f6-a6b6-471a-aa65-e1b5244a417e');
