INSERT INTO learning_records (customer_id, completed_sessions, case_interview_progress, case_interview_weaknesses, id) VALUES (E'a75b8863-8c75-4edc-a2bb-ace0ab3191ce', 0, E'新卒就活時に、一通りの対策を実施しました。戦略ファームでは、DIの内定をいただきました。フェルミは比較的問題ないと思いつつ、ケースに課題意識・苦手意識があります。', E'初期検討時間内で、的確に論点を絞ることが苦手です。結果としてディスカッションが発散する傾向にあります。(面接の雰囲気に飲まれてしまうことが多いです)', E'2d16b7f2-4a09-479c-8ccf-baea094f955c');
