INSERT INTO sales_pipeline (customer_id, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, sales_route, comparison_services, initial_channel, id) VALUES (E'8608b7c9-829b-4015-8eeb-10fb650a88ba', E'成約', 0, E'Xでの発信を読んで', E'実施', E'成約した\n立教→東工大ということもあり、周りに対策する人もいないことから、自分の立ち位置知れた方がいいかも→模擬テストルートで刺せた。\nもともとかなり契約する意図も高いお客さんでいいリードだった', E'2026-02-16', 1.0, E'2026-02-18', E'岡本', E'ただ、申し込みの際に、クレカが今ないという70%くらい嘘だろうってうそをつかれたので、入金までは気を抜かずにフォロー', E'X、エースアカウント、強い28卒の人が加入しているということが魅力的という話をしていた', E'LEAP, Prism', E'Xでの発信を読んで', E'a5a7f95f-b199-4104-8b8c-96f193f27010');
INSERT INTO sales_pipeline (customer_id, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, sales_route, comparison_services, initial_channel, id) VALUES (E'4faba8fb-75e4-47a8-b7ad-f208672e701c', E'検討中', 0, E'サービス説明ページを読んで', E'実施', E'全然ダメ、もっと対策しないとだめです感を出さないといけないなと実感\n完全に相手の可処分所得を増やすようなことができなかった\n\n5万円程度しか使えないといわれてしまい、親からも難しそうで終了', E'2026-02-10', 0.05, E'2026-02-17', E'岡本', E'フォローするが薄フォロー', E'インスタ広告きた！', E'比較なし', E'Facebook/インスタグラム', E'c7854927-4347-448e-8ca7-5af7c709accd');
INSERT INTO sales_pipeline (customer_id, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, sales_route, initial_channel, id) VALUES (E'1fe2a277-e4be-47de-9d15-57bb12dc79fc', E'追加指導', 0, E'サービス説明ページを読んで', E'実施', E'早くに対策を始めたいということかつ、かなり刺さっていたし、ライトを契約するうえで懸念点はないということだったが、一応他を見たいということで一回追加指導することになった。\n早く契約をしてもらうような選択肢もあったように思うが、他社と比較してもStrategistsが勝っていると思うので、そこは自信を持って追加指導で決める\n\n重視しているポイント\n成果が出るか、教材がしっかりしているか、自分の立ち位置がわかるか\n周りに26卒の内定者がいるが、就職してしまうので頼みにくい、同期と壁打ちするのはあまり意味がないように感じている\n有料コミュニティどこかには入ろうとしている\nBCAには外銀コースで選考を進めている', E'2026-02-11', 0.8, E'2026-03-08', E'岡本', E'追加指導で決める', E'X、エースアカウント', E'Acesの選考やイベント', E'fbe77834-d443-4fcf-8abb-180eb37ec7ae');
INSERT INTO sales_pipeline (customer_id, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, sales_route, comparison_services, initial_channel, id) VALUES (E'7b5e9cc8-5321-48bc-9e40-ec417fd052b1', E'失注見込', 0, E'不明', E'実施', E'ライトプランを成約した。足元キャッシュがないみたいで、月末には入るはずだが、親御さんにそれまでは借りないといけないということで、一旦未入金。\n求めているものとして、インプット教材を上げられていた（丁寧な回答例を含む）ので、丁寧に我々の価値を訴求して、理解をしてもらえた。\n一応有料メンターとか選コミュと比較をしている感じはあったが、テキストが唯一無二かつ、回答例とかカリキュラムの価値を訴求できたので、剥がせたはず。', E'2026-02-12', 1.0, E'2026-02-15', E'岡本', E'入金されるまで気を抜かない', E'X、エースアカウント', E'比較なし, 個人メンター', E'不明', E'04a231c6-5d3a-4e24-ad6b-4a7dcf928d60');
