INSERT INTO sales_pipeline (customer_id, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, sales_route, comparison_services, initial_channel, id) VALUES (E'ba515bc0-de8c-45cc-a157-7bf8b00598b0', E'成約', 298000, E'Xでの発信を読んで', E'実施', E'無事成約、入金も完了していることを確認\n求めているものを的確にヒアリングし、そのうえでそれを我々が提供できることを示せたことが勝因か\nかなりサービス説明で刺さっていた&本人も能力不足を理解していたので、ほめつつ不足を的確に指摘できたのもよかった', E'2026-02-06', 1.0, E'2026-02-06', E'岡本', E'葉山さんにアフターフォローは引き継いだが、いつでも質問とか乗れるようには準備', E'X、エースアカウントのツイートためになるって言ってくれた', E'Reverseだが、その場でStrategistsに決めてもらえた', E'Xでの発信を読んで', E'58aaba22-fa64-41c2-ae06-1063e45aa4da');
INSERT INTO sales_pipeline (customer_id, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, comparison_services, initial_channel, alternative_application, id) VALUES (E'74439783-bb87-490b-8018-16e79b9907f0', E'成約', 236800, E'Xでの発信を読んで', E'実施', E'・契約書と資料をものすごく細かくご覧になったうえで、具体的な質問をかなりされた\n・「ネットに価格が出ていない中で、自分に提示された価格が適正だとどう検証すればよいのか」と言われたので、お客様のご状況によって細かく提示できる条件が異なる中で誤解を生まないようにそうした運用になっていること・類似サービスも大体そうであることをお話。そのうえで、ぶっちゃけた話としてスクール、一部、フルの料金表を見せてフルをオファーしていることをお話したり、通常ではなく短期を進めてみるなどして懸念の解消に尽力した\n\n・フルーエージェント価格でエージェントありで受注したものの、過去1年以内に結構書類出してしまっているので留意されたい。前回はおっしゃっていなかったが今回発覚したのでここからプライステーブル変えるのもだいぶ分が悪いと思ったので、フル価格で決行した\n\n・日本の独立系ファーム、BIG４系戦略（モニターデロイト、EYP,　KPMG）、S&、RB、KNYは書類落ちが１年以内にある\n・ADLは出したか覚えてない\n・要するにMBBのみ\n\nIBMでSAP導入しておりコンサルに近いと言えば近いので、経験者っぽく採用してもらえないか。BainとBCGへの可能性が少しあるということでエージェント営業。\n\n当時期待したのにふたを開けたら全然エージェント顧客としての確度低いなという状況。ケースの指導はお金いただいているので頑張っていただきたいが、エージェントとしてそこまで力かけなくてOK。当たったらいいなというかんじ\n\n先方がかなり細かいので、メンターアサインはクレーム防止の観点でレスポンス得意な方を希望\n\n音楽が好き。昨日はアメリカから来た友達と渋谷のジャズバーに行った', E'2026-02-07', 1.0, E'2026-02-07', E'田中', E'この後すぐ契約書・入金対応するとおっしゃっているので、対応があるか追いかける。\nものすごく注意深い方なので、その場では無理だった。追加指導入れたしこのあとすぐ対応するという言質を最後に本人からとったので、これができるベスト', E'比較なし', E'Google検索', E'【未応募】 MBB 【二次面接落ち】 ローランド・ベルガー  【一次面接落ち】 デロイト モニター  【Webテスト落ち】 PwC Strategy&  【書類選考落ち】 アーサー・D・リトル、 A.T.カーニー 、EYストラテジー・アンド・コンサルティング 、KPMGコンサルティング', E'8b645d3d-587c-4025-ab77-2377070320c9');
