INSERT INTO sales_pipeline (customer_id, agent_interest_at_application, meeting_scheduled_date, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, agent_confirmation, comparison_services, initial_channel, id) VALUES (E'5ade8b8d-7ab0-4d49-b9f9-49de93ed026a', E'フォルトナ', E'未定', E'失注見込', 0, E'不明', E'実施', E'・最初入ってきたときになんか嫌な予感がしたのだが、Prism、Leap,ストラテジーアカデミーを検討していて、Prismにかなり心惹かれている様子だった\n・Prismは営業が学生で彼が優秀だったこと、中途には社会人メンターを確約していること、何より「本質的な思考力が鍛えられそうな雰囲気」に惹かれている様子→うちも＋5,000円で1回ずつ社会人メンターに変えられる旨、教科書などのインプットにはこだわっている旨はお伝えした\n・前回営業時は2月上旬で補助金前のテーブルだったので、今日来るまではほぼPrism一択だったところから、価格が変わったことでStrategistsも浮上して困ったなという感じだった。Prismに今質問している点がかえってきたら意思決定できるとのことで、Prism vs Strategistsで今週末までに結論を出すらしい\n・JST朝７時（本人が選んだのだが）だったので結構頭回ってなさそうだった\n・とりあえずフィードバックして、最後は力強く褒めて送り出した', E'2026-02-15', 0.6, E'2026-02-21', E'田中', E'教科書・動画講座の一部を送り、インプットの強さを伝える→対応済み\n今週末までに意思決定するとのことだったので、土曜日までに連絡なければフォローアップする。それまでにそれなりに質問がきそうなので、頑張って返す', E'検討できない', E'LEAP, Prism, Strategy Academy (ペンギン)', E'不明', E'e96755db-764b-4dde-bcc1-e5d2037878f1');
INSERT INTO sales_pipeline (customer_id, agent_interest_at_application, meeting_scheduled_date, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, agent_confirmation, comparison_services, initial_channel, alternative_application, id) VALUES (E'0f4f98c0-f1f6-4305-bdb4-4685bae48fb7', E'Stellar careers', E'2/13前後', E'成約', 246400, E'不明', E'未実施', E'・成約。スクール短期ー教科書動画講座すべての金額＋マッキンゼービヘイビア対策\n・2/13にBCGの面接を皮切りに、戦略７社の面接がバシバシ入ってくる予定。短期でがっつり対策したい\n・すでに教科書・動画講座はマッキンゼー分の含めてご購入いただいているので議論の練習していきたいご意向\n・現職コンサルかつ声とテンションが低めだったので冒頭でアイスブレークとかせずさっさと本題に入った\n・プランは通常とどちらがよいか聞かれたが、消化する時間もなさそうなので、あとで足す方も多いです、と相手に寄り添った提案にした（追加指導売れる可能性相応にあり）\n・ケースで心をつかむために、現職コンサルなのであえて結構ガンガン詰めてみたら、想定通りがっつり心をつかめた。「なんというか、本当に良い時間でした」としみじみおっしゃっていて、めちゃうれしかった\n・こちらから打診する前に短期で始めたい・マッキンゼービヘイビア対策もしたいと言ってくださった\n・ただ、ケースの筋はすこぶる悪いというか、ビジネスセンスみたいなところが結構弱含みなので、しっかりメンタリングしてください', E'2026-02-13', 1.0, E'2026-02-03', E'安西 先生', E'・メンターアサイン時は直近の空きがある方を優先してください', E'検討できない', E'比較なし', E'不明', E'マッキンゼー、BCG、カーニー、ベルガー、ADL、アク戦、ベイン（予定）', E'3587922a-2aba-4f40-8274-80c4a2e2c2c7');
