INSERT INTO sales_pipeline (customer_id, meeting_scheduled_date, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, agent_confirmation, initial_channel, id) VALUES (E'3db8ab07-8004-41c4-a196-0edf51140839', E'今年の2月後半', E'追加指導(NoShow)', 0, E'不明', E'noshow', E'・新卒JTB→現在Salesforce。2025年6月入社で、この1月にベストルーキー賞を受賞予定。現在、会社の業績がよくなく昇進が止まっているため、より成果に見合った報酬が得られる環境を求めてコンサルを見始めた\n・最短で7月入社を目指す\n・まずはMckの第二新卒枠での採用を狙い、2月に面接を受ける予定。他の戦略6社は転職意向高く、Mckの結果次第でそちらを来年の6月以降に受けていくとのこと\n・はじめはスクール料金で勝負しようとしたが、転職時期が後ろになるなら一部割引ならエージェント割もありだなと判断し、オファーした\n・先方的には「マッキンゼーの結果を待って、ダメだったら申し込もうかなと思っていた。御社にもエージェントとしてのうまみがないと思うので」と言われたので、マッキンゼーにそのまま受かって戻ってこない可能性＋他戦略を受ける際に別エージェントがついてしまう可能性をつぶすため、この段階でのエージェントでの獲得に舵を切った（わんきゃめさんも、来年６月まで待てば応募はできると言っているので）あくまでうちはスクールメインなので、ぜひマッキンゼーに受かってくれたらうれしい、と伝えておいた\n・ケースはいい感じだったので、「いい感じなので、ディスカッションですね～」という方向で持って行った。本人も納得感ありそうでメンタリング自体は普通に刺せている\n・今週いっぱい、別のエージェントにも話を聞いて判断したいとのことなので、日本時間日曜夜に追加指導予約済み"\n\n・初回営業の最後に、「正直に、めちゃくちゃやりたいです。今週中に他のエージェント見終わるのでそのあとに連絡させてください」→追加指導「ぜひお願いしたいです！」からのNo showなのでやや不思議。営業後も私が資料送る前に先方から「ありがとうございました！」的なLINEが来ていたのでちゃんとしている＋温度感も高かったので少し不思議（何かあったのだろうか）', E'2026-01-25', 0.8, E'2026-01-30', E'田中', E'興味があり、話を聞きたい', E'不明', E'2a90cc87-493c-448a-8784-2e18597d9129');
INSERT INTO sales_pipeline (customer_id, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, sales_person, sales_strategy, sales_route, comparison_services, initial_channel, id) VALUES (E'd65fe1c7-612c-4e86-a9e0-b08cbe82f454', E'NoShow', 0, E'不明', E'実施', E'外資系メーカー志望者がフェルミ面接対策目的で受けにきた。長期メンタリングを受ける気はなさそう。この時期総合コンサル志望者はもう少ないので営業も閉じたほうがいいかもしれない。', E'2026-01-24', 0.0, E'木村', E'せめて総合コンサル志望の人の指導がしたかった', E'ユーチューブ', E'比較なし', E'不明', E'f6ee787d-5edd-4d71-9c48-f7ff44a9a235');
INSERT INTO sales_pipeline (customer_id, agent_interest_at_application, meeting_scheduled_date, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, agent_confirmation, comparison_services, initial_channel, id) VALUES (E'90551af3-b359-48e8-8e9c-a4faa9009d71', E'フォルトナ', E'2026年5月目安', E'成約', 396000, E'A8', E'実施', E'・冒頭のスモールトークで週末何していたか話したら、フォルトナから勧められた「マーケティングマインドの磨き方」を読んでいたとのこと→エージェントとの関係性がやはり強固であることが伝わってきたので、エージェントはがしはしない方向性を継続\n・私も大前研一氏の本をいくつか紹介して、しばし雑談\n・場が温まってきたので、ケースに入る前に何か質問あるか聞いたら、サービスへの質問が出てきたので、ケースやっている間に手続きしておきたいから先に契約書・入金してからケースにしましょうと持っていて入金までを完了\n・よい印象を持ってほしいため、フェルミとケースはしっかりFBしていく（これを記載している今はThinking Time中）', E'2026-01-31', 1.0, E'2026-01-31', E'田中', E'想定通りのスムーズな営業', E'検討が難しいが、念の為話を聞きたい', E'比較なし', E'Google検索', E'0521bee8-d39a-4185-b1ec-2b5855375240');
INSERT INTO sales_pipeline (customer_id, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, sales_route, comparison_services, initial_channel, id) VALUES (E'8b2b7fd7-bd90-4ba4-b9b4-04ab5382bc05', E'成約', 54420, E'Xでの発信を読んで', E'実施', E'そもそもかなり確度が高かったため、XやGDイベントを始めとしたイベントの効果が明確に出ていると思います。ありがとうございます。\n\n営業においては、トークにおける濃淡を意識しながら話をすることで、伝えるべきポイントを探ることができたと思う。\nアイスブレイクにて、足元の選コミュ選考において大規模なところには入りたいと目標は明確であり、1.なるべく短い時間軸で成果を残せること 2.コスパがいいこと、を重点的にお伝えするように心がけた。\n\n選コミュプランの説明までは軽く説明し、相手が話したり考え方を整理する時間を作れるよう誘導をおこった点が良かった(自認ではあるが)と思う。\n\nまた、GD練習会が今日あるとのことだったので、Tipsをいくつかお伝えしたり、誠実でいいサービスなんだな、と思ってもらえるように意識した点も加点になった気がする。1to1のコミュニケーションであることは今後も強く意識したい。', E'2026-01-21', 0.8, E'2026-01-21', E'吉田', E'・トークにおける濃淡を意識\n・アイスブレイクで要点を早いタイミングで掴む\n・誠実に対応する', E'エーシーアカウントから', E'LEAP, Prism', E'X', E'c86d6184-5133-48f9-8c1b-c8db91716e7b');
INSERT INTO sales_pipeline (customer_id, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, sales_route, comparison_services, initial_channel, id) VALUES (E'bc2ea625-5cc3-4933-80b9-3c328bb40f0d', E'成約', 54420, E'メール', E'実施', E'AcesのGD選考から来てくださった。これもうまくナーチャリングができていたため、スムーズに会話できた。\n\n感触はかなり良い。親御さんに相談して、難しければ自分で払う、とおっしゃっていただけたので、今日の19時の報告を待つ。\n\nやはり個人に寄り添った形で提案させていただく。他社様と比較されていないとのことだったが、明確に選コミュプランがありお得であること、スタンダードへの移行もでき、この金額を無駄にできないことを訴求できた点が刺さった。\n\nまた、アイスブレイクの際には、自分の就職活動の苦労を共有しながら、竹田様が感じられている課題点を誠実に解決できる/できないを伝えさせていただくことができ、うまくグリップできた気がする。', E'2026-01-21', 0.8, E'2026-01-21', E'吉田', E'・誠実に向き合う(解決できる課題、できない課題を明確に伝える）\n・個人としてもサポートしたい旨を伝える(南野が言ってた通りにしてみる)', E'Acesの本選考GD', E'比較なし', E'メール', E'08674755-43cd-44ec-a27d-fed004779544');
INSERT INTO sales_pipeline (customer_id, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, sales_route, comparison_services, initial_channel, id) VALUES (E'26f3b9aa-cefb-4d41-8cc6-78beeef0b0ee', E'成約', 54420, E'Acesの選抜コミュニティに落ちたため', E'実施', E'まずまずのでき。別業界を志望されており、夏のタイミングでコンサルの内定は欲しい\n、との発言から、選コミュプランを提案し、総合コンサルに安定して受かるレベルまで対策しましょう。と提案させていただいた。\n\n今までの方とは少し会話のテンポが異なり、何度か喋り出しがかぶってしまったことがあったり、説明すべきポイントの要点が絞りきれていなかったりと、経験の浅さが同時に露呈したと感じる。一方で、ナーチャリングが進んでいたこともあり、ぜひ入りたいとの言葉を引き出すことができた。初回営業としてはまずまずの結果だと感じる。', E'2026-01-25', 0.6, E'2026-01-24', E'吉田', E'・相手によって話し方、トーン、テンポを意識すること\n・説明資料の使い方においても、伝えるべき要点を絞り、どのスライドでどの話を進めるのかの意識を強く持つことが重要だと感じた。', E'AcesのGD選考結果より', E'就活塾(コンサル関係なし)', E'X', E'e68631f7-72a6-4838-baa4-f6276d36dc03');
INSERT INTO sales_pipeline (customer_id, stage, projected_amount, decision_factor, deal_status, initial_channel, id) VALUES (E'aa3af924-a485-43ce-bf2a-f0d74186ed07', E'実施不可', 0, E'不明', E'未実施', E'不明', E'ec765859-ce12-4097-91c7-7646da2a062e');
INSERT INTO sales_pipeline (customer_id, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, sales_route, comparison_services, initial_channel, id) VALUES (E'2a42041c-5b9f-4e2e-b344-35fa382d0754', E'追加指導', 0, E'サービス説明ページを読んで', E'実施', E'厳しそうだったが、望みはあるという感じ。その場で決めきれなかったのがとても悔しい。\n\nお金がなくて親御さんと交渉後の追加指導だった。最初は親御さんと交渉してもダメでしたと言われるだけで望みも薄かったが、親御さんが「就活に過度にお金を払うのは嫌」というタイプだったので、今自分で使えるお金を就活に使い、プライベートの予定に対するお金を親御さんに出してもらうのはどうかと提案したところ、それなら可能性が全然あるとのこと。\n\n個人的にかなりグリップをできている感触があり、（当然断るのだが、）個人メンターとかでもダメですかとか言われるくらいなので、来月頭（3月のバイトのシフトが出て、お金のめどが立ちそうなタイミング）にもう一度話すことにした。\n\n親御さんに依存せずに自分でお金を出す、そのために3月のバイトを頑張るということで、30万も全然払えるなら払いたいという感じなので、最後まで追い切る。', E'2026-02-07', 0.6, E'2026-03-13', E'岡本', E'3/13にクイックに話を聞く', E'X', E'比較なし', E'Google検索', E'f6d2da8a-81b9-46f8-a6fe-49cbf1423fd7');
INSERT INTO sales_pipeline (customer_id, stage, projected_amount, decision_factor, deal_status, initial_channel, id) VALUES (E'f16c3a61-ad1b-4505-bf62-d4a9728df572', E'実施不可', 0, E'不明', E'未実施', E'不明', E'720b09d0-107b-4f63-bc9e-ca206a0822ab');
INSERT INTO sales_pipeline (customer_id, agent_interest_at_application, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, comparison_services, initial_channel, id) VALUES (E'dbd7912c-c164-4a54-906e-2ed212d8cc16', E'コンコード', E'成約', 396000, E'X', E'実施', E'・通常プランで成約したが、クラウドサインのシステムメンテナンスのせいで契約書と決済は未済。リスクは僅少だが。しっかり追いかける\n・東工大→都市計画系のブティックコンサル。めちゃくちゃ優秀。理路整然としていて素敵な方\n・高校時代から国際協力に関心あり、大学では土木系専攻。自分は都市や交通×国際で活躍したいと、日本工営という現職のみ就活で受けて内定\n・その後、自動運転のPOCなどの経験を積む中で、POCから実装に至らないところに課題を感じて、戦略コンサルを受けたいと思うにいたった。戦略７社＋IGPI（現職で子会社と働いて印象がよく、戦略コンサルを志望する理由にもなった）\n・コンコードと書類を仕上げており、練習企業を２月から、第一志望群（戦略７社）を４－５月に面接するスケジュールで進めたいとのこと。\n・本当はまだ書類出していなかったのでコンコードからはがしたかったが、かなり信頼関係がかたく失注に向かうリスクに鑑みて、提案にとどめた。', E'2026-01-18', 1.0, E'2026-01-19', E'田中', E'最後成約したのに、クラウドサインがシステムメンテナンス中で契約書ドラフトまで行ったが送付できず。日本の明朝、すぐに送付し、入金まで追いかける', E'比較なし', E'X', E'85f5c235-3e64-43b5-8d8e-3674b823040c');
