INSERT INTO sales_pipeline (customer_id, meeting_scheduled_date, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, agent_confirmation, comparison_services, initial_channel, id) VALUES (E'aa99292a-cd7f-4e10-9ca6-91a41b0699af', E'未定', E'成約', 396000, E'サービス説明ページを読んで', E'実施', E'・成約。契約書サイン済みだが入金は待ち。リスクは小さい\n\n・この方はアクセンチュア統合後に残るか、戦略コンサルに転職頑張るかが意思決定をかえると思ったので、最初の20分でアイスブレークしまくり、戦コンの働き方や報酬についての質問に答えまくった。場があたたまり「今日の最後に回答するつもりでしたが、ぜひ通常プランでお願いしたい」との申し出があったのでケースやる前に契約書を送ってサインいただいた。\n・クレカを切り替えるタイミングで新しいクレカで支払いたい（ポイント的に）ので決済は来週中にしてほしいと言われたので、来週末まで待つことにした。契約書はサインしてもらっているのでリスクは比較的低いが、決済までは油断しない\n・現年収1,070万円＋ボーナス200－300万円で、1,200－1,300万円→支払い能力高い\n・子供2人、明日から育休から復帰。子育てしながら戦略コンサルでやっていけるか気にしていたので、いかに働き方改革とコロナが働きやすい環境を実現したかを説明\n\n・HPに出てくる山本さんは誰なのか、と言われたので、略歴とともに手前みそですがめちゃくちゃ優秀な社長です、とお伝えした＋BCGの元マネとか総合コンサルに詳しい方とか、いろんな強い社会人が在籍しているとも補足した', E'2026-01-17', 1.0, E'2026-01-23', E'田中', E'この方は大丈夫だと思うが、決済をしっかり見届ける', E'検討が難しいが、念の為話を聞きたい', E'比較なし', E'Google検索', E'131427eb-d5d0-4527-bf3d-f3ae81865247');
