INSERT INTO sales_pipeline (customer_id, agent_interest_at_application, meeting_scheduled_date, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, agent_confirmation, comparison_services, initial_channel, id) VALUES (E'18b5f825-4c6c-4a1f-a982-16d01b9fc5e0', E'フォルトナ', E'未定', E'成約', 296000, E'サービス説明ページを読んで', E'実施', E'・フォルトナとがっちりで、３か月後くらいに出し始めたいなと思いながら対策中。入社時期にこだわりはないが、５－６月を目安に考えている\n・Kearney,DIくらいのティアにいきたい。総合の戦略もあり\n・学歴（明治大学）かつNTT東でコンサルは未経験なので、優秀だったがエージェントはがしは試みなかった。また、フォルトナとの信頼関係が固い\n\n・優秀な感じだったので、褒めつつまっとうにフィードバック。淡々としていたのでテンション上げるのに時間がかかったが、「いい質問ですね」とこちらがやや興奮して見せることで最後はよい雰囲気で解散\n・通常か短期か、依頼するかどうか、を検討中とのこと。価格に対する反応が見えなかったので安心できないが、確度は高い。失注するとしたら価格の一点', E'2026-01-08', 0.6, E'2026-01-08', E'田中', E'追加指導でも普通にちゃんとしたフィードバックをして訴求。できれば通常で閉じたいが、着地は短期かなという予感', E'検討できない', E'比較なし', E'Google検索', E'8c8f7c42-015c-41c3-bbb2-4b4339b0b732');
INSERT INTO sales_pipeline (customer_id, meeting_scheduled_date, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, agent_confirmation, comparison_services, initial_channel, id) VALUES (E'51131985-45c8-4c7e-87cb-0248e130851e', E'1月中', E'成約', 262050, E'サービス説明ページを読んで', E'実施', E'・ほとんどコンサル業界のことをご存じなく、ケースが何なのかもそもそもご存知なかったので、説明にかなり時間がかかった。\n・アクセンチュア、ディルバート、ノースサンド、あと１社の書類を自己応募し通過。１月中に面接があるのでケースが何か知りたくて来た\n・対策の必要性は訴求できてたが、「お金をかけてダメだった時が怖い」というハードルがかなり高い。そもそもコンサルへの想いが弱いため、受かることに対しても「通ったらラッキー」みたいな温度感（→コンサルの魅力と、このチャンスを逃さないことの大事さを伝えたが、刺さりきっていない）\n・次回はどこまで「自分、受かりそう！」「やっぱコンサル行きたい！」って思ってもらえるか、テンション上げきれるかがカギ\n・五分五分かなって感じ', E'2026-01-12', 0.4, E'2026-01-02', E'田中', E'追加指導でテンション上げきる\nあと、言ってないだけで属性考えると普通に価格がネックの可能性もあるので、分割払い可能は伝達済み', E'検討が難しいが、念の為話を聞きたい', E'比較なし', E'Google検索', E'7bbe2680-488c-4d41-adfb-58226c0e04be');
