INSERT INTO sales_pipeline (customer_id, agent_interest_at_application, meeting_scheduled_date, stage, projected_amount, decision_factor, deal_status, sales_content, sales_date, probability, response_date, sales_person, sales_strategy, agent_confirmation, comparison_services, initial_channel, id) VALUES (E'75ab5cf1-30c6-46be-bc6d-7c11015a466c', E'コンコード', E'未定', E'成約', 251100, E'サービス説明ページを読んで', E'実施', E'・その場でエージェントフル利用成約（通常プラン）.。契約書サイン済み、入金待ち・入塾フォームは送付済み。・ご本人の関心がエージェントのクオリティメインだったのでケースは一切やらずにヒアリング・業界知見の共有\n・エージェントの強みの訴求の面で成功事例を作れたので下記共有します：\nＬご本人がエージェントに不信感をお持ちで、誠実さを試されていた（前回の転職でムービンを起用。メディバというご本人の志望度が高かった医療コンサルに書類落ちしたと言われたが、自己応募のカジュアル面談は通ったという経緯があり不信感を抱いていた。田中さんがエージェントとして大切にされていることはなんですか？、との質問あり\nＬそこで、まず①弊社の出自はケース対策塾であり、その収入が安定していること、②キャリアエージェント個人のインセンティブ設計が完全な時給制であり、高いエージェントフィーを得たいという曲がったモチベーションが発生しない構造であること、を説明。次に個人の想いとして、③昔戦略ファームにいたころにたくさんの採用ミスマッチを見てきて、そうしたミスマッチをうむこと自体が社会悪だと思っている。業界を志す皆様の支援がしたくてやっているので、気持ちよく仕事ができる範囲を超えた仕事をすることはない、と説明。この回答後に「田中さんにお願いできますか？」との言葉をいただいた（特に②と③の理由でロジックとしての納得感＋個人の想いとしての誠実さを推したのが刺さった）\nＬそこで、「ご経歴を拝見した段階でぜひご支援したいと思っており、その場で自分が担当することを確約してよいか、社長にも事前に相談してきました」といったらかなり刺さりその場で「お願いします」となった\n\n・通過可能性的にもご本人の志向的にも、総合ファームが合いそうなのでBIG４中心に、シンクタンク系（NRIなど）も受けていく。BCGは出してもいいかも\n\n・メディバというファームに出したいとおっしゃっているのですが出せますかね？を確認する', E'2025-11-09', 1.0, E'2025-11-09', E'田中', E'エージェントとしての実績になるよう頑張りましょう。', E'興味があり、話を聞きたい', E'比較なし', E'ChatGPT', E'1c5f2689-d6d0-402c-be99-a26ddeaa5dd6');
